CREATE TABLE [dbo].[Disease_infection]
(
[Id] INT NOT NULL PRIMARY KEY IDENTITY(1,1),
[Disease_name] VARCHAR(MAX) NOT NULL,
[Other_names]  VARCHAR(MAX) NOT NULL,
[Signs]  VARCHAR(MAX) NOT NULL,
[Medication]  VARCHAR(MAX) NOT NULL,
[Treatment]  VARCHAR(MAX) NOT NULL,
[Infectous_control]  VARCHAR(MAX) NOT NULL,
[Rate] int NOT NULL,
[Symptoms] VARCHAR(MAX) NOT NULL,
)
